tag-all.map:
function (doc) {
  for(var i=0; i<doc.tags.length; i++) {
      emit(doc.tags[i], 1);
    }
  }

tag-published.map:
function (doc) {
  if(doc.published) {
    for(var i=0; i<doc.tags.length; i++) {
      emit(doc.tags[i], 1);
    }
  }
}

